
#pragma config(Motor,  port4,           intakeLeft,    tmotorNone, openLoop)
#pragma config(Motor,  port5,           intakeRight,   tmotorNone, openLoop)
#pragma config(Motor,  port6,           leftMotor,     tmotorServoContinuousRotation, openLoop, reversed)
#pragma config(Motor,  port7,           rightMotor,    tmotorServoContinuousRotation, openLoop, reversed)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

/*----------------------------------------------------------------------------------------------------*\
|*                           - Dual Joystick Control with Arm - 1 Remote -                            *|
|*                                      ROBOTC on VEX 2.0 Cortex                                      *|
|*                                                                                                    *|
|*  This program uses both the Left and the Right joysticks to run the robot using "tank control".    *|
|*  The Group 6 buttons on the top-right of the VEXnet Joystick are used to raise and lower an arm.   *|
|*                                                                                                    *|
|*                                        ROBOT CONFIGURATION                                         *|
|*    NOTES:                                                                                          *|
|*    1)  Ch1 is the X axis and Ch2 is the Y axis for the RIGHT joystick.                             *|
|*    2)  Ch3 is the Y axis and Ch4 is the X axis for the LEFT joystick.                              *|
|*                                                                                                    *|
|*    MOTORS & SENSORS:                                                                               *|
|*    [I/O Port]              [Name]              [Type]              [Description]                   *|
|*    Motor - Port 2          rightMotor           VEX Motor           Right motor                    *|
|*    Motor - Port 3          leftMotor            VEX Motor           Left motor                     *|
|*    Motor - Port 6          armMotor             VEX Motor           Arm motor                      *|
\*----------------------------------------------------------------------------------------------------*/

//+++++++++++++++++++++++++++++++++++++++++++++| MAIN |+++++++++++++++++++++++++++++++++++++++++++++++

task main()
{
	bool fast = true;
  while(1 == 1)
  {
    //Driving Motor Control
  if(fast){
    motor[leftMotor] = vexRT[Ch3] ;
    motor[rightMotor] = vexRT[Ch2] ; 
}else {
motor[leftMotor] = vexRT[Ch3]/2 ;
    motor[rightMotor] = vexRT[Ch2]/2 ;
}
    //Arm Control
    if(vexRT[Btn5U] == 1)
    {
    	fast = true;
    }
    	if(vexRT[Btn5D] == 1)
    {
    	fast = false;
    }
    
    
    if(vexRT[Btn6U] == 1)
    {
      motor[intakeLeft] = 255;
      
      motor[intakeRight] = 255;
    }
    
    else if(vexRT[Btn6D] == 1)
    {
      motor[intakeLeft] = 50;
      
      motor[intakeRight] = 50;
    }
    else
    {
     motor[intakeLeft] = 0;
      
      motor[intakeRight] = 0;
    }
  }
}
//++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
